import { useContext } from "react";
import { CurrentUserContext } from "../contexts/CurrentUserContext";

function Card({ card, onCardClick, onCardLike, onCardDelete }) {
  const currentUser = useContext(CurrentUserContext);
  console.log("Card data in Card component:", card);
  const cardWithLikes = {
    ...card,
    likes: card.likes || [],
  };
  const isOwn = cardWithLikes.owner?._id === currentUser?._id;
  const isLiked = cardWithLikes.likes.some((i) => i._id === currentUser?._id);

  const cardLikeButtonClassName = `element__like ${
    isLiked ? "element__like_active" : ""
  }`;

  function handleClick() {
    onCardClick(cardWithLikes);
  }

  function handleDeleteClick() {
    onCardDelete(cardWithLikes);
  }

  function handleLikeClick() {
    onCardLike(cardWithLikes);
  }

  return (
    <article className="element">
      <img
        className="element__image"
        src={cardWithLikes.link}
        alt={cardWithLikes.name}
        onClick={handleClick}
      />
      <h2 className="element__title">{cardWithLikes.name}</h2>
      <div className="element__like-container">
        <button
          aria-label="Like"
          className={cardLikeButtonClassName}
          type="button"
          onClick={handleLikeClick}
        ></button>
        <span className="element__like-counter">
          {cardWithLikes.likes.length}
        </span>
      </div>
      {isOwn && (
        <button
          aria-label="Delete"
          className="element__delete"
          type="button"
          onClick={handleDeleteClick}
        />
      )}
    </article>
  );
}

export default Card;
