import { useState, useContext, useEffect } from "react";
import { useForm } from "../hooks/useForm";
import {CurrentUserContext} from '../contexts/CurrentUserContext';
import {PopupWithForm} from "./PopupWithForm";

export function EditProfilePopup({isOpen, onClose, onUpdateUser, buttonText}){
    const currentUser = useContext(CurrentUserContext);
    const {values, handleChange, setValues} = useForm({});

    useEffect(() => {
        setValues(values);
      }, [currentUser]); 

    function handleSubmit(e){
        e.preventDefault();
        onUpdateUser(values);
    };

    return(
        <PopupWithForm 
          title = 'Редактировать профиль' 
          name = 'edit-profile' 
          isOpen = {isOpen} 
          onClose = {onClose}
          onSubmit = {handleSubmit}
          buttonText={buttonText}>
            <label className="popup__field" >
                <input type="text" value={name || ''} id="name-input" className="popup__input popup__input_field_name" 
                    name="edit-name" placeholder="Имя" required minLength="2" maxLength="40" onChange={handleChange}/>
                <span className="popup__input-error name-input-error"></span>
            </label>
            <label className="popup__field">
                <input type="text" value={description || ''} id="job-input" className="popup__input popup__input_field_job" 
                    name="edit-job" placeholder="О себе" required minLength="2" maxLength="200" onChange={handleChange}/>
                <span className="popup__input-error job-input-error"></span>
            </label>
        </PopupWithForm>
    )
}
